targets.expr:
time() - node_boot_time_seconds{
  job="node_exporter",
  deployed_site="$site",
  type="$type",
  name="$device"
}

1000 * node_time_seconds{
  job="node_exporter",
  deployed_site="$site",
  type="$type",
  name="$device"
}

max by (name) (
  node_hwmon_temp_celsius{
    job="node_exporter",
    deployed_site="$site",
    type="$type",
    name="$device"
  }
)

100 - (
  avg by (name) (
    irate(
      node_cpu_seconds_total{
        job="node_exporter",
        deployed_site="$site",
        type="$type",
        name="$device",
        mode="idle"
      }[5m]
    )
  ) * 100
)

100 - (
  avg by (name) (
    irate(
      node_cpu_seconds_total{
        job="node_exporter",
        deployed_site="$site",
        type="$type",
        name="$device",
        mode="idle"
      }[5m]
    )
  ) * 100
)

100 * avg by (mode) (
  irate(
    node_cpu_seconds_total{
      job="node_exporter",
      deployed_site="$site",
      type="$type",
      name="$device",
      mode!="idle"
    }[5m]
  )
)

node_hwmon_temp_celsius{
  job="node_exporter",
  deployed_site="$site",
  type="$type",
  name="$device"
}

100 * (
  1 - (
    node_memory_MemAvailable_bytes{
      job="node_exporter",
      deployed_site="$site",
      type="$type",
      name="$device"
    }
    /
    node_memory_MemTotal_bytes{
      job="node_exporter",
      deployed_site="$site",
      type="$type",
      name="$device"
    }
  )
)

rate(node_disk_read_bytes_total{
  job="node_exporter",
  deployed_site="$site",
  type="$type",
  name="$device"
}[5m])

rate(node_disk_written_bytes_total{
  job="node_exporter",
  deployed_site="$site",
  type="$type",
  name="$device"
}[5m])

sum by (device) (
  rate(node_network_receive_bytes_total{
    job="node_exporter",
    deployed_site="$site",
    type="$type",
    name="$device",
    device!~"lo"
  }[5m])
)

sum by (device) (
  rate(node_network_transmit_bytes_total{
    job="node_exporter",
    deployed_site="$site",
    type="$type",
    name="$device",
    device!~"lo"
  }[5m])
)
